import{x as e,e as a,o,s as t}from"./chunks/framework.D285Ok5z.js";const m=JSON.parse('{"title":"Privacy Policy","frontmatter":{"dynamic":true,"title":"Privacy Policy","page_type":"docs","slug":"privacy-policy","status":"published","sort":1,"user_created":"796b4fbf-a0d9-4eac-998a-49bf3f9ffe65","date_created":"2023-09-10T07:42:55.759Z","user_updated":"796b4fbf-a0d9-4eac-998a-49bf3f9ffe65","date_updated":"2023-09-10T07:43:16.955Z"},"headers":[],"params":{"slug":"privacy-policy","status":"published","sort":1,"user_created":"796b4fbf-a0d9-4eac-998a-49bf3f9ffe65","date_created":"2023-09-10T07:42:55.759Z","user_updated":"796b4fbf-a0d9-4eac-998a-49bf3f9ffe65","date_updated":"2023-09-10T07:43:16.955Z","title":"Privacy Policy"},"relativePath":"docs/privacy-policy/index.md","filePath":"docs/privacy-policy/index.md"}'),i={name:"docs/privacy-policy/index.md"},s=t('<p>This Privacy Policy explains how shop.chromatone.center collects, uses, and protects the personal information of its users. By using our website, you consent to the collection and use of your personal data as described in this policy.</p><h2 id="_1-information-we-collect" tabindex="-1">1. Information We Collect <a class="header-anchor" href="#_1-information-we-collect" aria-label="Permalink to &quot;1. Information We Collect&quot;">​</a></h2><p>We collect the following types of personal information:</p><ul><li>Full Name: We collect the full name of our customers for digital goods delivery.</li><li>Email Address: We collect email addresses for digital goods delivery and communication purposes.</li><li>Postal/Billing Address: We collect postal/billing addresses for physical goods delivery.</li><li>Anonymous Statistics: We collect anonymous statistics about website usage on our own statistics server. We do not share this data with Google or other data mining corporations.</li></ul><h2 id="_2-use-of-cookies-and-other-technologies" tabindex="-1">2. Use of Cookies and Other Technologies <a class="header-anchor" href="#_2-use-of-cookies-and-other-technologies" aria-label="Permalink to &quot;2. Use of Cookies and Other Technologies&quot;">​</a></h2><p>We use cookies and other similar technologies for the following purposes:</p><ul><li>LocalStorage: We use browser LocalStorage to store cart sessions for a convenient shopping experience between site visits.</li><li>Anonymous Visitor Count: We use cookies for anonymous visitor count to track the number of visitors to our website.</li></ul><h2 id="_3-third-party-sharing" tabindex="-1">3. Third-Party Sharing <a class="header-anchor" href="#_3-third-party-sharing" aria-label="Permalink to &quot;3. Third-Party Sharing&quot;">​</a></h2><p>We may share certain information with third parties for the following purposes:</p><ul><li>Transaction Data: We transfer transaction data securely via Stripe for payment processing. We do not share this information with anyone.</li><li>Customer Address: We may publish approximate (1 km radius) coordinates of the customer address to show on the map on the shop website.</li></ul><h2 id="_4-data-security-measures" tabindex="-1">4. Data Security Measures <a class="header-anchor" href="#_4-data-security-measures" aria-label="Permalink to &quot;4. Data Security Measures&quot;">​</a></h2><p>We take the security of your data seriously and have implemented appropriate measures to protect it. These measures include:</p><ul><li>Secure Servers: We store customer data in our internal server database, which is protected by industry-standard security measures.</li><li>Limited Access: No third parties have access to customer data.</li></ul><h2 id="_5-user-rights-and-choices" tabindex="-1">5. User Rights and Choices <a class="header-anchor" href="#_5-user-rights-and-choices" aria-label="Permalink to &quot;5. User Rights and Choices&quot;">​</a></h2><p>We provide users with the following rights and choices regarding their personal data:</p><ul><li>Access, Correction, and Deletion: Customers can request access, correction, or deletion of their personal data by contacting us at support@chromatone.center.</li></ul><h2 id="_6-contact-information" tabindex="-1">6. Contact Information <a class="header-anchor" href="#_6-contact-information" aria-label="Permalink to &quot;6. Contact Information&quot;">​</a></h2><p>If you have any questions or concerns regarding our Privacy Policy, please contact us at support@chromatone.center.</p>',18),r=[s];function n(c,l,d,u,h,p){return o(),a("div",null,r)}const y=e(i,[["render",n]]);export{m as __pageData,y as default};
